// scripts/deploy.js
const hre = require("hardhat");

async function main() {
  // Compile and get the contract factory
  const TouristSafety = await hre.ethers.getContractFactory("TouristSafety");

  // Deploy the contract
  const touristSafety = await TouristSafety.deploy();
  await touristSafety.waitForDeployment();

  console.log("âœ… TouristSafety deployed to:", await touristSafety.getAddress());
}

// Recommended pattern for async/await errors
main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});
